Note  This should not be used directly since it applies to specific files   bgzip gwas_summary_temp_sorted
tabix -s 1 -b 2 -e 2 -S 1 gwas_summary_temp_sorted.gz
cat fourier_ls-all.bed | while read line
do
    array=(${line})
    chrnum=${array[0]#*chr}
    chrnum=${chrnum// /}
    start=${array[1]// /}
    end=${array[2]// /}
    if [  ${start}  !=  start  ]; then
    tabix gwas_summary_temp_sorted.gz ${chrnum}:${start}-${end}   output.${chrnum}_${start}_${end}
done
